mapper.update:
		update t_e_auction set
		name=#{name},starttime=#{starttime},endtime=#{endtime},modifytime=now(),
        modifier=#{modifier},businessid=#{businessid},description=#{description},
        status=#{status}, type=#{type}, isShow=#{isShow}
		where id=#{id}

update t_e_auction set modifytime=now()

    ,name=#{name}


    ,starttime=#{starttime}


    ,endtime=#{endtime}


    ,modifier=#{modifier}


    ,businessid=#{businessid}


    ,description=#{description}


    ,status=#{status}


    ,type=#{type}


    ,isShow=#{isShow}

where id=#{id}

mapper.select:
select count(1) from t_e_auction t
where 1=1 and t.status != '0'


        and t.name like CONCAT('%', #{name}, '%')


        and t.isShow like CONCAT('%', #{isShow}, '%')


        and t.status = #{status}


        and t.businessid = #{businessid}

select , su1.nickname as creatorName,su2.nickname as modifierName, b.name as businessName, wu.nickname as wxName, wu.avatarUrl path, (select count(1) from t_con_item_res r where r.conid=t.id and r.contype='1') as picCount, (select path from t_con_item_res where t.id=conid and contype='1' order by idx asc limit 1) as logopath, (select width from t_con_item_res where t.id=conid and contype='1' order by idx asc limit 1) as width, (select height from t_con_item_res where t.id=conid and contype='1' order by idx asc limit 1) as height, c.name as typeName from t_e_auction t left join sys_user su1 on t.creator=su1.id left join sys_user su2 on t.modifier=su2.id left join t_e_business b on t.businessid=b.id left join weixin_user wu on wu.wxid=t.wxid left join wx_code c on c.code = t.type where 1=1 and t.status != '0'
and t.name like CONCAT('%', #{name}, '%')
and t.isShow like CONCAT('%', #{isShow}, '%')
and t.status = #{status}
and t.businessid = #{businessid}
order by t.modifytime desc
${pager.orderCondition}
${pager.mysqlQueryCondition}

select
,
(select path from t_con_item_res where t.id=conid and contype='1' order by idx asc limit 1) as logopath,
(select width from t_con_item_res where t.id=conid and contype='1' order by idx asc limit 1) as width,
(select height from t_con_item_res where t.id=conid and contype='1' order by idx asc limit 1) as height,
(select count(1) from t_e_fabulous f where f.fabulousid=t.id and f.fabuloustype='2') as fabulousNum

    ,case when f.wxid is not null then '1' else '0' end as isFabulous

from t_e_auction t

    left join t_e_fabulous f on f.fabulousid=t.id and f.fabuloustype='2' and f.wxid=#{fabulousWxid}

where (1=2

    or t.wxid = #{wxid}


    or t.businessid = #{businessId}


    or t.id IN

        #{id}

) and t.status !='0'
order by t.starttime desc
