mapper.update:
update t_e_blacklist set id=#{id}

  ,creatorid=#{creatorid}


  ,type=#{type}


  ,blackid=#{blackid}

where id=#{id}

mapper.select:
select count(1) from t_e_blacklist t
where 1=1


    and t.creatorid = #{creatorid}

select.if:
select , u.nickname, '1' as status from t_e_blacklist t, weixin_user u where 1=1 and t.blackid = u.wxid and t.type='1'
and u.nickname like CONCAT('%', #{nickname}, '%')
order by t.createtime desc
${pager.orderCondition}
${pager.mysqlQueryCondition}

select u.id, u.wxid as blackid, u.nickname, (case when t.blackid is not null then '1' else '0' end) as status from weixin_user u left join t_e_blacklist t on t.blackid=u.wxid and t.type='1' where 1=1
and not exists(select 1 from t_e_blacklist t where t.blackid = u.wxid and t.type='1')
and u.nickname like CONCAT('%', #{nickname}, '%')
order by status desc, u.id asc
${pager.orderCondition}
${pager.mysqlQueryCondition}

mapper.select:
select

from t_e_blacklist t
where 1=1


    and t.creatorid = #{creatorid}


order by t.createtime desc

  ${pager.orderCondition}


  ${pager.mysqlQueryCondition}
